/**
 * @param {Function} BaseController
 * @param {Object} Command
 *
 * @returns {Function}
 */
module.exports = function(BaseController, Command) {
    return BaseController.extend({

        /**
         * @param {Object} request
         * @param {Object} response
         */
        handle: function (request, response) {
            response.writeHead(200, {
                'Content-Type': 'application/json'
            });

            var jsonString = '{"action":"DoeIets","params":{"eerste":"test","tweede":3}}';

            //@todo anders opzetten. Geen REST API meer, maar een command API

            var myCommand = Command.createFromJSON(jsonString);

            response.end(myCommand.toJSON());
        }
    });
};