/**
 * @param {Function} BaseController
 * @returns {Function}
 */
module.exports = function(BaseController) {

    if (typeof BaseController === 'undefined') {
        throw new Error('HomeController module requires BaseController to work');
    }

    return BaseController.extend({

        /**
         * @param {Object} request
         * @param {Object} response
         * @param {Object} router
         */
        handle: function (request, response, router) {
            response.writeHead(200, {
                'Content-Type': 'application/json'
            });

            var data = {
                content: 'Page detail!',
                id: router.getCurrentRoute().getParam('id')
            };

            response.end(JSON.stringify(data));
        }
    });
};