/**
 * @param {Function} BaseRouter
 * @param {Function} RouteLiteral
 * @param {Function} RouteParam
 * @param {Function} HomeController
 * @param {Function} PageDetailController
 * @param {Function} ExecuteController
 *
 * @returns {Function}
 */
module.exports = function(BaseRouter, RouteLiteral, RouteParam, HomeController, PageDetailController, ExecuteController) {

    if (typeof BaseRouter === 'undefined') {
        throw new Error('BaseRouter is required for the Router module to work');
    }

    if (typeof RouteLiteral === 'undefined') {
        throw new Error('RouteLiteral is required for the Router module to work');
    }

    if (typeof HomeController === 'undefined') {
        throw new Error('HomeController is required for the Router module to work');
    }

    if (typeof PageDetailController === 'undefined') {
        throw new Error('PageDetailController is required for the Router module to work');
    }

    return BaseRouter.extend({

        /**
         * Configures this router
         */
        _configure: function() {
            this.setRoutes({
                home: new RouteLiteral('/', [ 'GET' ], HomeController, null),
                pageDetail: new RouteParam('/page/:id', [ 'GET' ], PageDetailController, null),
                execute: new RouteLiteral('/execute', [ 'GET' ], ExecuteController, null)
            });

            BaseRouter.prototype._configure.apply(this, arguments);
        }
    });
};