"use strict";

/**
 * @param {Function} Inheritance
 * @returns {Function}
 */
module.exports = function(Inheritance) {

    if (typeof Inheritance === 'undefined') {
        throw new Error('Inheritance module is required for the LoggerInterface module to work');
    }

    var Interface = require('gn/interface')(Inheritance);
    return Interface.extend({

        /**
         * Configures this interface
         */
       _configure: function() {
            this._require('logError', 'function');
            this._require('logLog', 'function');
            this._require('logWarn', 'function');
            this._require('logInfo', 'function');
       }
    });
};